export default function LocationSelector() {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phone: "",
    district: "",
    block: "",
    village: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
      ...(name === "district" && { block: "", village: "" }),
      ...(name === "block" && { village: "" }),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); 

    try {
      const response = await fetch("/api/send-email", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (response.ok) {
        toast.success("Email sent successfully!");
        setFormData({
          firstname: "",
          lastname: "",
          email: "",
          phone: "",
          district: "",
          block: "",
          village: "",
        });
      } else {
        toast.error(`Error: ${data.error}`);
      }
    } catch (error) {
      console.error("Error:", error);
      toast.error("Failed to send email.");
    } finally {
        setLoading(false); // Set loading to false after submission
    }
  };

  return (

    <div className="bg-gray-100 text-gray-900 dark:bg-gray-900 dark:text-white">
            {/* Hero Section */}
            <section className="relative py-24 flex items-center justify-center text-center bg-[#58130a] text-white overflow-hidden">
                <motion.div
                    initial={{ opacity: 0, y: -50 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 1 }}
                    className="container mx-auto px-6"
                >
                    <h1 className="text-5xl font-bold">Contact Us</h1>
                    <p className="mt-4 text-lg max-w-3xl mx-auto">
                    Let's bring a library to your village – get in touch today!
                    </p>
                </motion.div>
            </section>
            <form className="max-w-lg mx-auto mt-5 p-4" onSubmit={handleSubmit}>

            <div className="flex justify-between">
                {["firstname", "lastname"].map((field, i) => (
                <div key={i} className='w-[45%]'>
                    <label className="block mt-4 mb-2 text-sm font-medium text-gray-900">
                    {field === "firstname" ? "First Name" : "Last Name"}
                    </label>
                    <input
                    required
                    type="text"
                    name={field}
                    value={formData[field]}
                    onChange={handleChange}
                    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-[#58130a] focus:border-[#58130a] block w-full p-2.5"
                    />
                </div>
                ))}
            </div>

            <div className="flex justify-between">
                {["email", "phone"].map((field,i) => (
                <div key={i} className='w-[45%]'>
                    <label className="block mt-4 mb-2 text-sm font-medium text-gray-900">
                    {field.charAt(0).toUpperCase() + field.slice(1)}
                    </label>
                    <input
                    {...(field === "email" ? { required: true } : {})} 
                    type={field === "email" ? "email" : "tel"}
                    name={field}
                    value={formData[field]}
                    onChange={handleChange}
                    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-[#58130a] focus:border-[#58130a] block w-full p-2.5"
                    />
                </div>
                ))}
            </div>

            <div>
                {[  
                { label: "District", name: "district", options: Object.keys(countryStateCityData) },
                { label: "Block", name: "block", options: countryStateCityData[formData.district] ? Object.keys(countryStateCityData[formData.district]) : [], disabled: !formData.district },
                { label: "Village", name: "village", options: formData.block ? countryStateCityData[formData.district]?.[formData.block] || [] : [], disabled: !formData.block },
                ].map(({ label, name, options, disabled }, i) => (
                <div key={i}>
                    <label className="block mt-4 mb-2 text-sm font-medium text-gray-900">{label}</label>
                    <select
                    name={name}
                    value={formData[name]}
                    onChange={handleChange}
                    required
                    disabled={disabled}
                    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-[#58130a] focus:border-[#58130a] block w-full p-2.5"
                    >
                    <option value="">{`Select ${label}`}</option>
                    {options.map((option,i) => (
                        <option key={i} value={option} className="hover:bg-red-500">
                        {option}
                        </option>
                    ))}
                    </select>
                </div>
                ))}
            </div>
            <div className='mb-3'>
            {/* <button
                type="submit"
                className="cursor-pointer inline-flex h-12 animate-shimmer items-center justify-center rounded-md border border-white bg-[#58130a] hover:bg-[#cf7b70] px-6 font-medium text-white transition-colors focus:outline-none focus:ring-2 focus:ring-slate-400 focus:ring-offset-2 focus:ring-offset-slate-50 w-full mt-2"
            >
                Submit
            </button> */}

             <button
                type="submit"
                disabled={loading} // Disable the button when loading
                className={`cursor-pointer inline-flex h-12 items-center justify-center rounded-md border border-white bg-[#58130a] hover:bg-[#cf7b70] px-6 font-medium text-white transition-colors focus:outline-none focus:ring-2 focus:ring-slate-400 focus:ring-offset-2 focus:ring-offset-slate-50 w-full mt-2 ${loading ? "opacity-50" : ""}`}
             >
                {loading ? (
                <div className="animate-spin w-5 h-5 border-4 border-t-transparent border-white rounded-full"></div>
                ) : (
                "Submit"
                )}
            </button>

            </div>
            </form>
</div>
  );
}
